function create_ship() {
    let space_ship = document.createElement('div');
    space_ship.style.position = "absolute";
    space_ship.style.bottom = "20px";
    space_ship.style.width = "150px";
    space_ship.style.height = "50px";
    space_ship.style.border = "line";
    space_ship.style.backgroundColor = "#d30319";
    space_ship.id = "spaceship";
    space_ship.style.cursor = "crosshair";
    document.body.appendChild(space_ship);


    document.addEventListener('mousemove', move_ship);
    document.addEventListener('click', create_bullet);
}

setInterval(move_bullets, 140);
setInterval(create_enemy, 1300);
setInterval(bullet_enemy_collision,140)


function create_enemy() {
    let enemy = document.createElement('div');
    enemy.style.position = "absolute";
    enemy.style.top = getRandomInt(10, 30) + "%";
    enemy.style.left = getRandomInt(10, 90) + "%";
    enemy.style.border = "thick solid #0000FF";

    enemy.style.width = getRandomInt(50, 250) + "px";
    enemy.style.height = getRandomInt(20, 70) + "px";
    enemy.style.backgroundColor = "#17d300";
    enemy.id = "enemy";
    enemy.classList.add('enemy');
    enemy.style.cursor = "crosshair";
    document.body.appendChild(enemy);


}

function move_ship(e) {
    let space_ship = document.querySelector("#spaceship");
    space_ship.style.left = e.pageX - 75 + "px";
    space_ship.innerText = e.pageX;
}

function create_bullet(e) {
    let bullet = document.createElement('div');
    bullet.classList.add('bullet');
    bullet.style.bottom = '20px';
    document.body.appendChild(bullet);

    bullet.style.left = e.pageX - 10 + "px";
    beep();
}

function bullet_enemy_collision() {

    let bullets = document.querySelectorAll(".bullet");
    let enemies = document.querySelectorAll(".enemy");


    for (let bullet of bullets) {
        let blt_pos = bullet.getBoundingClientRect();
        for (let enemy of enemies) {
            let eny_pos = enemy.getBoundingClientRect();
            if (!(blt_pos.top > eny_pos.bottom ||
                blt_pos.right < eny_pos.left ||
                blt_pos.bottom < eny_pos.top ||
                blt_pos.left > eny_pos.right)) {
                bullet.remove();
                enemy.remove();
            }


        }
    }
}



function move_bullets() {
    let bullets = document.querySelectorAll('.bullet');

    for (let bullet of bullets) {
        const currentPosition = parseInt(bullet.style.bottom);

        if (currentPosition < 1000) {
            bullet.style.bottom = (currentPosition + 40) + "px";
        } else {
            bullet.remove()
        }
    }
}

function beep() {
    const snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min; //The maximum is exclusive and the minimum is inclusive
}

create_ship();